import React, { useState } from "react";

const App = () => {
    let [text, setText] = useState();
    let onChange = (e) => {
        text = e.target.value;
    }
    let onClick = (e) => {
        // e.preventDefault();
        setText(text);
        document.getElementById('greeting').value = '';
    }

    return (
        <>
            <h2>Hello {text}</h2>
            <form id="form" onSubmit={onClick}>
                <input id="greeting" type='text' onChange={onChange}></input>
                <button  >Click Me</button>
            </form>
        </>
    );
};

export default App;
