import React, { useState } from "react";

const App = ()=>{
    const [bg,setBg] = useState('red');
    const [text,setText] = useState("CLICK ME");
    const changeBgColor = ()=>{
        setBg('yellow');
        setText(' CLICK ME');
    }
    const changeAgain = ()=>{
        setBg('red');
    }
    return (
        <>
            <button style={{
                backgroundColor : bg
            }} onClick={changeBgColor} onDoubleClick={changeAgain}>{text}</button>
        </>
    );
};

export default App;
