import React, { useState } from "react";

const App = () => {
    let [fullName, setfullName] = useState({
        firstName:"",
        lastName:""
    });

    let onChangeEvent= (e) => {
        setfullName((prevValue)=>{
            console.log(prevValue);
            // if(e.target.name === "first")
        })
    }
    let onSubmit = (e) => {
        e.preventDefault();
    }

    return (
        <>
            <h2>Hello </h2>
            <form id="form" onSubmit={onSubmit}>
                <input className="greeting" type='text' onChange={onChangeEvent} placeholder="Enter Your First Name" name="firstName" ></input>
                <input className="greeting" type='text' placeholder="Enter Your Last Name" onChange={onChangeEvent} name='lastName'></input>
                <br />
                <button type="submit">Click Me</button>
            </form>
        </>
    );
};

export default App;
