import React from "react";
let localDate=0;
let localTime=0;
setInterval(() => {
    
    let date = new Date();
     localDate = date.toLocaleDateString();
     localTime = date.toLocaleTimeString();
}, 1000);
function Para() {
    return (
        <>
            <p>{`Current Date  is ${localDate}`}</p>
            <p>{`Current Time  is ${localTime}`}</p>
        </>
    )
}

export default Para;
