import React from "react";
const heading = {
    color: "black",
    fontWeight: 600
}

const greetingColor = {

}
let greeting = "";
let date = new Date();

let hours = date.getHours();
if (hours >= 1 && hours <= 12) {
    greeting = 'Good Morning';
    greetingColor.color = 'red';
} else if (hours > 12 && hours <= 15) {
    greeting = "Good Afternoon ";
    greetingColor.color = 'yellow';

} else if (hours > 15 && hours <= 21) {
    greeting = "Good Evening";
    greetingColor.color = 'blue';

} else {
    greeting = "Good Night";
    greetingColor.color = 'green';

}
function Headings() {
    return (
        <>

            <h1 className="heading" style={heading}>Hello <span style={greetingColor}>{greeting}</span></h1>
            <h2 className="heading" style={heading} >My name is Akalesh Tanwer</h2>
        </>
    )
}

export default Headings;
