import React, { useState } from "react";

const App = () => {
    let [fName, setfName] = useState('');
    let [cfName, setcfName] = useState('');
    let [sName, setsName] = useState('');
    let [csName, setcsName] = useState('');
    let onChangeName = (e) => {
        setfName(e.target.value)
    }
    // let changeSecondName = (e) => {
    //     secondName = e.target.value;

    // }
    let onSubmit = (e) => {
        e.preventDefault();
        setcfName(fName);
        // setSecondName(secondName);
    }

    return (
        <>
            <h2>Hello {cfName}</h2>
            <form id="form" onSubmit={onSubmit}>
                <input className="greeting" type='text' onChange={onChangeName} placeholder="Enter Your First Name" value={fName}></input>
                {/* <input className="greeting" type='text' placeholder="Enter Your Second Name" onChange={changeSecondName}></input> */}
                <br />
                <button type="submit">Click Me</button>
            </form>
        </>
    );
};

export default App;
