import React from "react";
import ReactDom from "react-dom";
import "./index.css";


let date = new Date();
let localDate = date.toLocaleDateString()
let hours = date.ge
let localTime = date.toLocaleTimeString()
let img1 = 'https://picsum.photos/401'
let img2 = 'https://picsum.photos/399'
let img3 = 'https://picsum.photos/402'
const randomImages = {
  display: 'flex',
  justifyContent: 'space-between',
  margin: '20px 0px'
}
const heading = {
  color: "black",
  fontWeight: 600
}
const greeting= "";
ReactDom.render(
  <>
    <h1 className="heading" style={heading}>Hello <span>{greeting}</span></h1>
    <h1 className="heading" style={heading} >My name is Akalesh Tanwer</h1>
    <p>{`Current Date  is ${localDate}`}</p>
    <p>{`Current Time  is ${localTime}`}</p>
    <div className="randomImages" style={randomImages}>
      <img src={img1} alt="randomImages" />
      <img src={img3} alt="randomImages" />
      <img src={img2} alt="randomImages" />
    </div>
  </>,
  document.getElementById('root')
);
