import React, { useState } from "react";

const App = () => {
    let [firstName, setText] = useState();
    let onChange = (e) => {
        firstName = e.target.value;
    }
    let onClick = (e) => {
        e.preventDefault();
        setText(firstName);
        document.querySelectorAll('.greeting').value = '';
    }

    return (
        <>
            <h2>Hello {firstName}</h2>
            <form id="form" onSubmit={onClick}>
                <input className="greeting" type='text' onChange={onChange} placeholder="Enter Your First Name"></input>
                <input className="greeting" type='text' onChange={onChange} placeholder="Enter Your First Name"></input>
                <button  >Click Me</button>
            </form>
        </>
    );
};

export default App;
