import React, { useState } from "react";

const App = () => {
    let [name, setName] = useState('');
    let [fName, setfName] = useState('');
    let onChangeName = (e) => {
    setName(e.target.value) 
    }
    // let changeSecondName = (e) => {
    //     secondName = e.target.value;

    // }
    let onSubmit = (e) => {
        e.preventDefault();
        setfName(name);
        // setSecondName(secondName);
    }

    return (
        <>
            <h2>Hello {fName}</h2>
            <form id="form" onSubmit={onSubmit}>
            <input className="greeting" type='text' onChange={onChangeName} placeholder="Enter Your First Name" value={name}></input>
            {/* <input className="greeting" type='text' placeholder="Enter Your Second Name" onChange={changeSecondName}></input> */}
            <br/>
            <button  type="submit">Click Me</button>
            </form>
        </>
    );
};

export default App;
