import React, { useState } from "react";
function App() {
    const [count , setdata] = useState(0) ;
    function IncreaseValue(){
        return setdata(count++);
    }
    return (
        <>
            <h1>{count}</h1>
            <button  onClick={IncreaseValue}>Click Me</button>
        </>
    )
};


export default App;
