import React, { useState } from "react";

const App = () => { 
    const [text,setText] = useState();
    let onChange = (e)=>{
         text = e.target.value;
    }

    return (
        <>
            <h2>Hello {text}</h2>
            <div id="form">
                <input type='text' onChange={onChange}></input>
                <button >Click Me</button>
            </div>
        </>
    );
};

export default App;
